mapper.update:
update activity_enroll_num
LEFT JOIN activity ON activity.id = activity_enroll_num.activity_id
set enroll_num = enroll_num + 1
where activity_id = #{activityId}
AND activity_enroll_num.enroll_num < activity.enroll_num_limit;

update activity_enroll_num
LEFT JOIN activity ON activity.id = activity_enroll_num.activity_id
set enroll_num = enroll_num - 1
where activity_id = #{activityId}
AND activity_enroll_num.enroll_num > 0;
