mapper.select:
SELECT

FROM portal_organizer_user


    and INSTR(username,#{queryForm.keyword})


    and deleted_flag = #{queryForm.deletedFlag}


    and disabled_flag = #{queryForm.disabledFlag}


    and school_id = #{queryForm.schoolId}

mapper.update:
update portal_organizer_user set deleted_flag = #{deletedFlag}
where id in

    #{item}

update portal_organizer_user set disabled_flag = #{disabledFlag}
where id in

    #{item}
