mapper.select:
SELECT ru.role_id
FROM t_role_user ru
WHERE ru.user_id = #{userId}

SELECT backend_user.id, backend_user.username, backend_user.password, backend_user.disabled_flag, backend_user.update_time, backend_user.create_time FROM t_role_user left join backend_user on t_role_user.id = backend_user.id left join t_role on t_role_user.role_id = t_role.role_id
AND INSTR(backend_user.username,#{queryForm.keywords})
AND t_role_user.role_id = #{queryForm.roleId}

mapper.delete:
DELETE
FROM t_role_user
WHERE role_id = #{roleId}
  and user_id = #{userId}

DELETE FROM t_role_user
WHERE role_id = #{roleId} and user_id in

    #{item}
